var stepSection,//触点
    stepSlider;//面板
$(function ($) {
    stepSection = $(".stepSection"),

        stepSlider = $(".stepSlider").slick({
            arrows: !1,
            autoplay: !1,
            autoplaySpeed: 2e3,
            infinite: !1
        }),

        $(".dappSlider").slick({
            prevArrow: ".dapp_slider_holder .slider_prev",
            nextArrow: ".dapp_slider_holder .slider_next",
            autoplay: !0,
            arrows: !0,
            autoplaySpeed: 2e4,
            infinite: !1
        }),

        stepSlider.on("beforeChange", function (event, slick, currentSlide, nextSlide) {
            stepSection.attr("data-step", nextSlide + 1)
        }),

        $(".stepBtn").on("click", function () {
            var firedEl = $(this),
                ind     = firedEl.index();
            return stepSlider.slick("slickGoTo", parseInt(ind)), !1
        }),


        $(".discoverBtn").on("click", function () {
            return scrollDoc(browserWindow.height() - nav_container.height(), 800), !1
        })
});